const diameterButton=document.getElementById("btn-diameter"),circumButton=document.getElementById("btn-circum"),areaButton=document.getElementById("btn-area"),diameterForm=document.getElementById("diameter"),circumForm=document.getElementById("circumference"),areaForm=document.getElementById("area"),resultBox=document.querySelector(".result-box"),resultBoxText=document.querySelector(".result-box .result"),resultBoxButton=document.querySelector(".result-box .btn-ok"),resultBoxOverlay=document.querySelector(".overlay"),closeButton=document.querySelectorAll(".btn-close"),formulaForm=document.querySelectorAll(".formula");function diameterCalc(){const e=document.querySelector(".diameter-input");let t=e.value;if(0===t.length)e.classList.add("invalid");else{e.classList.remove("invalid");let r=parseFloat(2*t);resultBox.classList.add("active"),resultBoxOverlay.classList.add("active"),resultBoxText.innerHTML=`D = 2 x r <br> = 2 x ${t} <br> = ${r}`,e.value=""}}function circumCalc(){const e=document.querySelector(".circum-input");let t=e.value;if(0===t.length)e.classList.add("invalid");else{e.classList.remove("invalid");let r=parseFloat(6.28*t);resultBox.classList.add("active"),resultBoxOverlay.classList.add("active"),resultBoxText.innerHTML=`C = 2 x π x r <br> = 2 x 3.14 x ${t} <br> = ${r}`,e.value=""}}function areaCalc(){const e=document.querySelector(".area-input");let t=e.value;if(0===t.length)e.classList.add("invalid");else{e.classList.remove("invalid");let r=parseFloat(3.14*t*t);resultBox.classList.add("active"),resultBoxOverlay.classList.add("active"),resultBoxText.innerHTML=`A = π x r² <br> = 3.14 x ${t} x ${t} <br> = ${r}`,e.value=""}}diameterButton.addEventListener("click",()=>{diameterForm.classList.add("active")}),circumButton.addEventListener("click",()=>{circumForm.classList.add("active")}),areaButton.addEventListener("click",()=>{areaForm.classList.add("active")}),closeButton.forEach(e=>{e.addEventListener("click",()=>{formulaForm.forEach(e=>{e.classList.remove("active")}),document.querySelectorAll("input").forEach(e=>{e.classList.remove("invalid")})})}),resultBoxButton.addEventListener("click",()=>{resultBox.classList.remove("active"),resultBoxOverlay.classList.remove("active")});
